changeSet.sql:
CREATE TABLE IF NOT EXISTS spar_windows
(
    point_id   BIGSERIAL PRIMARY KEY,
    point_name VARCHAR(255),
    time_start VARCHAR(255),
    time_end   VARCHAR(255)
    );

-- Опционально: индексы для улучшения производительности
CREATE INDEX IF NOT EXISTS idx_spar_windows_point_name ON spar_windows(point_name);

-- Опционально: комментарии
COMMENT ON TABLE spar_windows IS 'Окна времени для точек Spar';
COMMENT ON COLUMN spar_windows.point_id IS 'Уникальный идентификатор точки';
COMMENT ON COLUMN spar_windows.point_name IS 'Название точки';
COMMENT ON COLUMN spar_windows.time_start IS 'Время начала окна (формат HH:MM)';
COMMENT ON COLUMN spar_windows.time_end IS 'Время окончания окна (формат HH:MM)';
